            // myWork.push(e)
            //is all the businesses of the list of things that you can retrieve
            // }
            // This code doesn't even get reached?
            //ans.push(e.name, e.distance, e.image_url, e.categories) // to retrieve these specific values
          })
          .catch(function (error) {
            console.error(error);
          });
      // let myNewJSON = {table:[]}
      // let json = JSON.stringify(myWork);
      //
      // let fs = require('fs');
      // fs.writeFile('myjsonfile.json', json, 'utf8', callback);
      console.log(myWork)
      return myWork
    }
  }

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p onClick={yelpHandler}>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
