import axios from 'axios';

// Have to allow cross-control access
// Have to open terminal and use the following lines (make sure in open terminal)
// open -na Google\ Chrome --args --user-data-dir=/tmp/temporary-chrome-profile-dir --disable-web-security

//inputOpenAt is if the location is open in 30 minutes or not
export function getRestInformation(inputLocation, inputPrice, inputOpenAt) {
    const options = {
        method: 'GET',
        url: 'https://api.yelp.com/v3/businesses/search',
        params: {
            location: inputLocation,
            price: inputPrice,
            open_at: inputOpenAt,
            sort_by: 'distance',
            radius: 8047,
            limit: '1'
        },
        headers: {
            accept: 'application/json',
            // Access_Control_Allow_Origin: "*",
            // Access_Control_Allow_Headers: "Origin, X-Requested-With, Content-Type, Accept",
            Authorization:
                'Bearer l5_sN8Upkix19lLgwHxpAMThs0G2RBKr0rNRPxJ7ZmlOFagJ9EMi_1vv9o0BJNsto0R0hU1enjBhinEDROi6zODQWf_Tdzk2Lhq47-Z1Xz8EP2EEmjws8MyqSp-LY3Yx'
        }
    };
    // Want to get name, distance, image_url, categories
    let ans = []
    axios
        .request(options)
        .then(function (response) {
            let e = response.data.businesses[0];
            // ans.push(e) is all the businesses of the list of things that you can retrieve
            ans.push(e.name, e.distance, e.image_url, e.categories)
            return e.name
        })
        .catch(function (error) {
            console.error(error);
        });
    // return ans
}
